mapper.select:
select
id, user_id, start_place, start_time, start_type, end_place, end_time, end_type, sign_type, create_time,
work_hours
from final_job.check_in
where enabled = true

    and user_id = #{userId}


    and create_time between #{beginTime} and #{tailTime}

order by create_time desc
limit #{offset}, #{pageSize}

select id, user_id, start_place, start_time, start_type, end_place, end_time, end_type, sign_type, create_time, work_hours from final_job.check_in where enabled = true
and user_id = #{userId}
and create_time between #{beginTime} and #{tailTime}

select count(*)
from final_job.check_in
where enabled = true

    and user_id = #{userId}


    and create_time between #{beginTime} and #{beginTime}

select
id, user_id, start_place, start_time, start_type, end_place, end_time, end_type, sign_type, create_time,
work_hours
from final_job.check_in


        and id = #{id}


        and user_id = #{userId}


        and start_place = #{startPlace}


        and start_time = #{startTime}


        and start_type = #{startType}


        and end_place = #{endPlace}


        and end_time = #{endTime}


        and end_type = #{endType}


        and sign_type = #{signType}


        and create_time = #{createTime}


        and work_hours = #{workHours}
