mapper.select:
select
check_in.id, user_id, start_place, start_time, start_type, end_place, end_time, end_type, sign_type, check_in.create_time,
work_hours
from check_in,user
where check_in.user_id = user.id and check_in.enabled = true

    and user_id = #{userId}


    and check_in.create_time between #{beginTime} and #{tailTime}


    and department_id = #{departmentId}



    and real_name like #{realName}

order by check_in.create_time desc
limit #{offset}, #{pageSize}

select
id, user_id, start_place, start_time, start_type, end_place, end_time, end_type, sign_type, create_time, work_hours
from final_job.check_in
where enabled = true

    and user_id = #{userId}


    and create_time between #{beginTime} and #{tailTime}

select count(*)
from check_in,user
where check_in.user_id = user.id and check_in.enabled = true

    and user_id = #{userId}


    and check_in.create_time between #{beginTime} and #{tailTime}


    and department_id = #{departmentId}



    and real_name like #{realName}

select
id, user_id, start_place, start_time, start_type, end_place, end_time, end_type, sign_type, create_time,
work_hours
from final_job.check_in


        and id = #{id}


        and user_id = #{userId}


        and start_place = #{startPlace}


        and start_time = #{startTime}


        and start_type = #{startType}


        and end_place = #{endPlace}


        and end_time = #{endTime}


        and end_type = #{endType}


        and sign_type = #{signType}


        and create_time = #{createTime}


        and work_hours = #{workHours}
