mapper.select:
select
contract.id, user_id, begin_time, end_time, content, confirm
from contract,user
where contract.user_id = user.id

    and work_id = #{workId}


    and department_id = #{departmentId}


    and work_state = #{workState}


    and confirm = #{confirm}



    and real_name like #{realName}

order by contract.confirm,create_time desc
limit #{offset},#{pageSize}

select count(*)
from contract,user
where contract.user_id = user.id

    and work_id = #{workId}


    and department_id = #{departmentId}


    and work_state = #{workState}


    and confirm = #{confirm}



    and real_name like #{realName}

select
  id, user_id, begin_time, end_time, content, confirm
from final_job.contract


        and id = #{id}


        and user_id = #{userId}


        and begin_time = #{beginTime}


        and end_time = #{endTime}


        and content = #{content}


        and confirm = #{confirm}
